import dotenv from 'dotenv';
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

// Load environment variables
dotenv.config();

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen(port, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import { setupAuth, authenticateToken, optionalAuth, requireAdmin, requireModerator } from "./auth";
import { insertUserSchema, insertRoomSchema, insertStorySchema } from "@shared/schema";
import { z } from "zod";
import OpenAI from "openai";
import multer from "multer";

interface WebSocketClient extends WebSocket {
  userId?: string;
  roomId?: string;
}

export async function registerRoutes(app: Express): Promise<Server> {
  const httpServer = createServer(app);

  // Setup authentication
  setupAuth(app);

  // OpenAI client setup
  const apiKey = process.env.OPENAI_API_KEY?.trim();
  console.log('OpenAI API Key status:', apiKey && apiKey.length > 0 ? `Present (${apiKey.length} chars)` : 'Missing or empty');
  let openai: OpenAI | null = null;

  if (apiKey && apiKey.length > 0) {
    try {
      openai = new OpenAI({ 
        apiKey: apiKey 
      });
      console.log('OpenAI client initialized successfully');
    } catch (error) {
      console.error('Failed to initialize OpenAI client:', error);
    }
  } else {
    console.log('OpenAI client not initialized - API key missing or empty');
  }

  // Multer setup for file uploads
  const upload = multer({ storage: multer.memoryStorage() });

  // WebSocket setup
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  const clients = new Set<WebSocketClient>();
  const roomClients = new Map<string, Set<WebSocketClient>>();

  wss.on('connection', (ws: WebSocketClient) => {
    clients.add(ws);

    ws.on('message', async (message) => {
      try {
        const data = JSON.parse(message.toString());

        switch (data.type) {
          case 'join-room':
            if (ws.roomId && roomClients.has(ws.roomId)) {
              roomClients.get(ws.roomId)!.delete(ws);
            }

            ws.roomId = data.roomId;
            ws.userId = data.userId;

            if (!roomClients.has(data.roomId)) {
              roomClients.set(data.roomId, new Set());
            }
            roomClients.get(data.roomId)!.add(ws);

            // Update room member count
            const roomSize = roomClients.get(data.roomId)!.size;
            await storage.updateRoomMemberCount(data.roomId, 0); // Set to actual count
            break;

          case 'story-added':
            // Broadcast new story to room members
            if (ws.roomId && roomClients.has(ws.roomId)) {
              const roomMembers = roomClients.get(ws.roomId)!;
              roomMembers.forEach(client => {
                if (client.readyState === WebSocket.OPEN) {
                  client.send(JSON.stringify({
                    type: 'new-story',
                    story: data.story,
                    chainId: data.chainId
                  }));
                }
              });
            }
            break;
        }
      } catch (error) {
        console.error('WebSocket message error:', error);
      }
    });

    ws.on('close', () => {
      clients.delete(ws);
      if (ws.roomId && roomClients.has(ws.roomId)) {
        roomClients.get(ws.roomId)!.delete(ws);
        if (roomClients.get(ws.roomId)!.size === 0) {
          roomClients.delete(ws.roomId);
        }
      }
    });
  });

  // Auth routes
  app.get('/api/auth/user', authenticateToken as any, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Admin routes for user management
  app.get('/api/admin/users', requireAdmin as any, async (req, res) => {
    try {
      const users = Array.from((storage as any).users.values());
      res.json(users);
    } catch (error) {
      res.status(500).json({ message: 'Server error', error });
    }
  });

  app.patch('/api/admin/users/:id/role', requireAdmin as any, async (req, res) => {
    try {
      const { role } = req.body;
      if (!['user', 'moderator', 'admin'].includes(role)) {
        return res.status(400).json({ message: 'Invalid role' });
      }

      const user = await storage.getUser(req.params.id);
      if (!user) {
        return res.status(404).json({ message: 'User not found' });
      }

      // Update user role
      const updatedUser = await storage.upsertUser({ 
        id: user.id,
        email: user.email,
        firstName: user.firstName,
        lastName: user.lastName,
        profileImageUrl: user.profileImageUrl,
        username: user.username,
        role 
      });
      res.json(updatedUser);
    } catch (error) {
      res.status(500).json({ message: 'Server error', error });
    }
  });

  // Auth middleware (simplified for MVP)
  const requireAuth = async (req: any, res: any, next: any) => {
    const userId = req.headers['x-user-id'];
    if (!userId) {
      return res.status(401).json({ message: 'Authentication required' });
    }
    const user = await storage.getUser(userId as string);
    if (!user) {
      return res.status(401).json({ message: 'User not found' });
    }
    req.user = user;
    next();
  };

  // Users
  app.post('/api/users', async (req, res) => {
    try {
      const userData = insertUserSchema.parse(req.body);

      // Check if user already exists
      if (userData.email) {
        const existingUser = await storage.getUserByEmail(userData.email);
        if (existingUser) {
          return res.status(400).json({ message: 'User already exists' });
        }
      }

      const user = await storage.createUser(userData);
      res.json(user);
    } catch (error) {
      res.status(400).json({ message: 'Invalid user data', error });
    }
  });

  app.get('/api/users/:id', async (req, res) => {
    try {
      const user = await storage.getUser(req.params.id);
      if (!user) {
        return res.status(404).json({ message: 'User not found' });
      }
      res.json(user);
    } catch (error) {
      res.status(500).json({ message: 'Server error', error });
    }
  });

  // Rooms
  app.post('/api/rooms', requireAuth, async (req, res) => {
    try {
      const roomData = insertRoomSchema.parse({
        ...req.body,
        creatorId: (req.user as any)?.claims?.sub || "",
      });

      const room = await storage.createRoom(roomData);
      res.json(room);
    } catch (error) {
      res.status(400).json({ message: 'Invalid room data', error });
    }
  });

  app.get('/api/rooms/public', async (req, res) => {
    try {
      const rooms = await storage.getPublicRooms();
      res.json(rooms);
    } catch (error) {
      res.status(500).json({ message: 'Server error', error });
    }
  });

  app.get('/api/rooms/code/:code', async (req, res) => {
    try {
      const room = await storage.getRoomByCode(req.params.code);
      if (!room) {
        return res.status(404).json({ message: 'Room not found' });
      }
      res.json(room);
    } catch (error) {
      res.status(500).json({ message: 'Server error', error });
    }
  });

  // Stories
  app.post('/api/stories', requireAuth, async (req, res) => {
    try {
      if (!req.user) {
        return res.status(401).json({ message: 'User not authenticated' });
      }

      const storyData = insertStorySchema.parse({
        ...req.body,
        authorId: (req.user as any).id,
        authorName: (req.user as any).username || (req.user as any).email || "Anonymous",
      });

      const story = await storage.createStory(storyData);
      res.json(story);
    } catch (error) {
      res.status(400).json({ message: 'Invalid story data', error });
    }
  });

  app.get('/api/stories/chains', async (req, res) => {
    try {
      const limit = parseInt(req.query.limit as string) || 10;
      const chains = await storage.getLatestStoryChains(limit);
      res.json(chains);
    } catch (error) {
      res.status(500).json({ message: 'Server error', error });
    }
  });

  app.get('/api/stories/chain/:chainId', async (req, res) => {
    try {
      const chainId = parseInt(req.params.chainId);
      const stories = await storage.getStoriesByChain(chainId);
      res.json(stories);
    } catch (error) {
      res.status(500).json({ message: 'Server error', error });
    }
  });

  app.post('/api/stories/:id/heart', requireAuth, async (req, res) => {
    try {
      const userId = (req.user as any)?.claims?.sub || (req.user as any)?.id;
      const isHearted = await storage.toggleHeart(req.params.id, userId);
      res.json({ hearted: isHearted });
    } catch (error) {
      res.status(500).json({ message: 'Server error', error });
    }
  });

  app.get('/api/stories/next-chain-id', async (req, res) => {
    try {
      const chainId = await storage.getNextChainId();
      res.json({ chainId });
    } catch (error) {
      res.status(500).json({ message: 'Server error', error });
    }
  });

  // Themes
  app.get('/api/themes/daily', async (req, res) => {
    try {
      const theme = await storage.getDailyTheme();
      res.json(theme);
    } catch (error) {
      res.status(500).json({ message: 'Server error', error });
    }
  });

  app.get('/api/themes', async (req, res) => {
    try {
      const themes = await storage.getAllThemes();
      res.json(themes);
    } catch (error) {
      res.status(500).json({ message: 'Server error', error });
    }
  });

  // Community
  app.get('/api/community/stats', async (req, res) => {
    try {
      const stats = await storage.getCommunityStats();
      res.json(stats);
    } catch (error) {
      res.status(500).json({ message: 'Server error', error });
    }
  });

  app.get('/api/community/cookies-picks', async (req, res) => {
    try {
      const picks = await storage.getCookiesPicks();
      res.json(picks);
    } catch (error) {
      res.status(500).json({ message: 'Server error', error });
    }
  });

  // AI Routes
  app.post('/api/ai/transcribe', upload.single('audio'), async (req: any, res) => {
    try {
      if (!openai) {
        return res.status(503).json({ message: 'AI features unavailable - OpenAI API key not configured' });
      }

      if (!req.file) {
        return res.status(400).json({ message: 'No audio file provided' });
      }

      const transcription = await openai.audio.transcriptions.create({
        file: new File([req.file.buffer], 'audio.wav', { type: 'audio/wav' }),
        model: 'whisper-1',
      });

      res.json({ text: transcription.text });
    } catch (error) {
      console.error('Transcription error:', error);
      res.status(500).json({ message: 'Transcription failed', error });
    }
  });

  app.post('/api/ai/continue-story', async (req, res) => {
    try {
      if (!openai) {
        return res.status(503).json({ message: 'AI features unavailable - OpenAI API key not configured' });
      }

      const { storyContext } = req.body;

      if (!storyContext || typeof storyContext !== 'string') {
        return res.status(400).json({ message: 'Story context is required' });
      }

      // the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
      const completion = await openai.chat.completions.create({
        model: "gpt-4o",
        messages: [
          {
            role: "system",
            content: "You are a creative writing assistant for a collaborative storytelling platform inspired by Nemrah Ahmed's work. Generate a natural, engaging continuation for the story that follows the established tone and style. Keep continuations between 50-150 words, maintaining the narrative flow. Focus on emotional depth and character development."
          },
          {
            role: "user",
            content: `Continue this story naturally: "${storyContext}"`
          }
        ],
        max_tokens: 200,
        temperature: 0.8,
      });

      const continuation = completion.choices[0].message.content?.trim() || '';
      res.json({ continuation });
    } catch (error) {
      console.error('Story continuation error:', error);
      res.status(500).json({ message: 'Story continuation failed', error });
    }
  });

  // Export Routes
  app.post('/api/export/pdf', async (req, res) => {
    try {
      const { chainId, title } = req.body;

      if (!chainId) {
        return res.status(400).json({ message: 'Chain ID is required' });
      }

      const stories = await storage.getStoriesByChain(parseInt(chainId));
      const fullStory = stories.map(story => story.content).join(' ');

      // For now, return a simple text response
      // In a real implementation, you'd use a PDF generation library like puppeteer
      res.setHeader('Content-Type', 'application/pdf');
      res.setHeader('Content-Disposition', `attachment; filename="${title || 'story'}.pdf"`);
      res.send(`PDF Export\n\nTitle: ${title || 'Untitled Story'}\n\nContent:\n${fullStory}`);
    } catch (error) {
      console.error('PDF export error:', error);
      res.status(500).json({ message: 'PDF export failed', error });
    }
  });

  app.post('/api/export/image', async (req, res) => {
    try {
      const { chainId, title } = req.body;

      if (!chainId) {
        return res.status(400).json({ message: 'Chain ID is required' });
      }

      const stories = await storage.getStoriesByChain(parseInt(chainId));
      const fullStory = stories.map(story => story.content).join(' ');

      // For now, return a simple text response
      // In a real implementation, you'd use a canvas/image generation library
      res.setHeader('Content-Type', 'image/png');
      res.setHeader('Content-Disposition', `attachment; filename="${title || 'story'}.png"`);
      res.send(`Image Export\n\nTitle: ${title || 'Untitled Story'}\n\nContent:\n${fullStory}`);
    } catch (error) {
      console.error('Image export error:', error);
      res.status(500).json({ message: 'Image export failed', error });
    }
  });

  return httpServer;
}

server/storage.ts.content:
import { type User, type InsertUser, type UpsertUser, type Room, type InsertRoom, type Story, type InsertStory, type StoryChain, type CommunityStats, type Theme, type CookiesPick } from "@shared/schema";
import { randomUUID } from "crypto";
import { drizzle } from 'drizzle-orm/neon-http';
import { neon } from '@neondatabase/serverless';
import { users, rooms, stories, hearts, themes, cookiesPicks } from '@shared/schema';
import { eq, desc, sql, and } from 'drizzle-orm';

export interface IStorage {
  // Users (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  getUserByUsername(username: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUserStats(userId: string, contributions?: number, hearts?: number): Promise<void>;

  // Rooms
  getRoom(id: string): Promise<Room | undefined>;
  getRoomByCode(code: string): Promise<Room | undefined>;
  createRoom(room: InsertRoom): Promise<Room>;
  getPublicRooms(): Promise<Room[]>;
  updateRoomMemberCount(roomId: string, count: number): Promise<void>;

  // Stories
  getStory(id: string): Promise<Story | undefined>;
  createStory(story: InsertStory): Promise<Story>;
  getStoriesByChain(chainId: number): Promise<Story[]>;
  getStoriesByRoom(roomId: string): Promise<Story[]>;
  getLatestStoryChains(limit?: number): Promise<StoryChain[]>;
  toggleHeart(storyId: string, userId: string): Promise<boolean>;
  getNextChainId(): Promise<number>;

  // Themes
  getDailyTheme(): Promise<Theme | undefined>;
  getAllThemes(): Promise<Theme[]>;

  // Community
  getCommunityStats(): Promise<CommunityStats>;
  getCookiesPicks(): Promise<CookiesPick[]>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private rooms: Map<string, Room>;
  private stories: Map<string, Story>;
  private hearts: Map<string, Set<string>>; // storyId -> Set of userIds
  private themes: Map<string, Theme>;
  private cookiesPicks: Map<string, CookiesPick>;
  private chainCounter: number;

  constructor() {
    this.users = new Map();
    this.rooms = new Map();
    this.stories = new Map();
    this.hearts = new Map();
    this.themes = new Map();
    this.cookiesPicks = new Map();
    this.chainCounter = 1;

    this.initializeData();
  }

  private initializeData() {
    // Create sample themes
    const themes = [
      {
        id: randomUUID(),
        title: "Jannat Kay Pattay Vibes",
        description: "Stories about finding home in people, not places",
        prompt: "Someone somewhere is discovering that home isn't a place, but the people who make your heart feel at peace...",
        isDaily: true,
        isActive: true,
        createdAt: new Date(),
      },
      {
        id: randomUUID(),
        title: "Nemrah Ahmed Inspired",
        description: "Mystery and family secrets",
        prompt: "Someone somewhere just discovered a hidden truth that changes everything they thought they knew...",
        isDaily: false,
        isActive: true,
        createdAt: new Date(),
      }
    ];

    themes.forEach(theme => this.themes.set(theme.id, theme));
  }

  // Users
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.username === username);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.email === email);
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = {
      ...insertUser,
      id,
      contributionsCount: 0,
      heartsReceived: 0,
      experiencePoints: 0,
      level: 1,
      badges: [],
      preferences: {},
      createdAt: new Date(),
      updatedAt: new Date(),
    };
    this.users.set(id, user);
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const existingUser = this.users.get(userData.id!);
    if (existingUser) {
      const updatedUser = {
        ...existingUser,
        ...userData,
        updatedAt: new Date(),
      };
      this.users.set(updatedUser.id, updatedUser);
      return updatedUser;
    } else {
      return this.createUser(userData as InsertUser);
    }
  }

  async updateUserStats(userId: string, contributions = 0, hearts = 0): Promise<void> {
    const user = this.users.get(userId);
    if (user) {
      user.contributionsCount += contributions;
      user.heartsReceived += hearts;
      this.users.set(userId, user);
    }
  }

  // Rooms
  async getRoom(id: string): Promise<Room | undefined> {
    return this.rooms.get(id);
  }

  async getRoomByCode(code: string): Promise<Room | undefined> {
    return Array.from(this.rooms.values()).find(room => room.code === code);
  }

  async createRoom(insertRoom: InsertRoom): Promise<Room> {
    const id = randomUUID();
    const code = Math.random().toString(36).substring(2, 8).toUpperCase();
    const room: Room = {
      ...insertRoom,
      id,
      code,
      isPrivate: insertRoom.isPrivate || false,
      isThemed: insertRoom.isThemed || false,
      theme: insertRoom.theme || "",
      memberCount: 1,
      createdAt: new Date(),
    };
    this.rooms.set(id, room);
    return room;
  }

  async getPublicRooms(): Promise<Room[]> {
    return Array.from(this.rooms.values())
      .filter(room => !room.isPrivate)
      .sort((a, b) => b.memberCount - a.memberCount);
  }

  async updateRoomMemberCount(roomId: string, count: number): Promise<void> {
    const room = this.rooms.get(roomId);
    if (room) {
      room.memberCount = Math.max(0, room.memberCount + count);
      this.rooms.set(roomId, room);
    }
  }

  // Stories
  async getStory(id: string): Promise<Story | undefined> {
    return this.stories.get(id);
  }

  async createStory(insertStory: InsertStory): Promise<Story> {
    const id = randomUUID();
    const story: Story = {
      ...insertStory,
      id,
      roomId: insertStory.roomId || null,
      hearts: 0,
      comments: 0,
      createdAt: new Date(),
    };
    this.stories.set(id, story);

    // Update user contributions
    await this.updateUserStats(story.authorId, 1, 0);

    return story;
  }

  async getStoriesByChain(chainId: number): Promise<Story[]> {
    return Array.from(this.stories.values())
      .filter(story => story.chainId === chainId)
      .sort((a, b) => a.sequence - b.sequence);
  }

  async getStoriesByRoom(roomId: string): Promise<Story[]> {
    return Array.from(this.stories.values())
      .filter(story => story.roomId === roomId)
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
  }

  async getLatestStoryChains(limit = 10): Promise<StoryChain[]> {
    const chainGroups = new Map<number, Story[]>();

    // Group stories by chainId
    Array.from(this.stories.values()).forEach(story => {
      if (!chainGroups.has(story.chainId)) {
        chainGroups.set(story.chainId, []);
      }
      chainGroups.get(story.chainId)!.push(story);
    });

    // Convert to StoryChain objects
    const chains: StoryChain[] = Array.from(chainGroups.entries()).map(([chainId, stories]) => {
      const sortedStories = stories.sort((a, b) => a.sequence - b.sequence);
      const totalHearts = stories.reduce((sum, story) => sum + story.hearts, 0);
      const totalComments = stories.reduce((sum, story) => sum + story.comments, 0);
      const contributorCount = new Set(stories.map(s => s.authorId)).size;

      return {
        chainId,
        roomId: stories[0]?.roomId,
        stories: sortedStories,
        totalHearts,
        totalComments,
        contributorCount,
        createdAt: sortedStories[0]?.createdAt || new Date(),
      };
    });

    // Sort by latest activity and return limited results
    return chains
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime())
      .slice(0, limit);
  }

  async toggleHeart(storyId: string, userId: string): Promise<boolean> {
    if (!this.hearts.has(storyId)) {
      this.hearts.set(storyId, new Set());
    }

    const storyHearts = this.hearts.get(storyId)!;
    const story = this.stories.get(storyId);

    if (!story) return false;

    if (storyHearts.has(userId)) {
      // Remove heart
      storyHearts.delete(userId);
      story.hearts = Math.max(0, story.hearts - 1);
      await this.updateUserStats(story.authorId, 0, -1);
      return false;
    } else {
      // Add heart
      storyHearts.add(userId);
      story.hearts += 1;
      await this.updateUserStats(story.authorId, 0, 1);
      return true;
    }
  }

  async getNextChainId(): Promise<number> {
    return this.chainCounter++;
  }

  // Themes
  async getDailyTheme(): Promise<Theme | undefined> {
    return Array.from(this.themes.values()).find(theme => theme.isDaily && theme.isActive);
  }

  async getAllThemes(): Promise<Theme[]> {
    return Array.from(this.themes.values()).filter(theme => theme.isActive);
  }

  // Community
  async getCommunityStats(): Promise<CommunityStats> {
    const totalStories = this.stories.size;
    const activeUsers = this.users.size;
    const totalHearts = Array.from(this.stories.values()).reduce((sum, story) => sum + story.hearts, 0);

    // Daily contributions (stories from today)
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const dailyContributions = Array.from(this.stories.values())
      .filter(story => story.createdAt >= today).length;

    return {
      totalStories,
      activeUsers,
      totalHearts,
      dailyContributions,
    };
  }

  async getCookiesPicks(): Promise<CookiesPick[]> {
    return Array.from(this.cookiesPicks.values())
      .filter(pick => pick.isFeatured)
      .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
  }
}

// Database storage implementation
export class DatabaseStorage implements IStorage {
  private db: any;

  constructor() {
    if (!process.env.DATABASE_URL) {
      throw new Error("DATABASE_URL not found in environment variables");
    }
    const connection = neon(process.env.DATABASE_URL);
    this.db = drizzle(connection);
    this.initializeData();
  }

  private async initializeData() {
    try {
      // Check if themes exist, if not create them
      const existingThemes = await this.db.select().from(themes).limit(1);
      if (existingThemes.length === 0) {
        const sampleThemes = [
          {
            title: "Jannat Kay Pattay Vibes",
            description: "Stories about finding home in people, not places",
            prompt: "Someone somewhere is discovering that home isn't a place, but the people who make your heart feel at peace...",
            isDaily: true,
            isActive: true,
          },
          {
            title: "Nemrah Ahmed Inspired",
            description: "Mystery and family secrets",
            prompt: "Someone somewhere just discovered a hidden truth that changes everything they thought they knew...",
            isDaily: false,
            isActive: true,
          },
        ];

        await this.db.insert(themes).values(sampleThemes);
      }
    } catch (error) {
      console.log("Failed to initialize themes:", error);
    }
  }

  // Users
  async getUser(id: string): Promise<User | undefined> {
    const result = await this.db.select().from(users).where(eq(users.id, id)).limit(1);
    return result[0];
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const result = await this.db.select().from(users).where(eq(users.username, username)).limit(1);
    return result[0];
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const result = await this.db.select().from(users).where(eq(users.email, email)).limit(1);
    return result[0];
  }

  async createUser(user: InsertUser): Promise<User> {
    const result = await this.db.insert(users).values(user).returning();
    return result[0];
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const result = await this.db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          email: userData.email,
          firstName: userData.firstName,
          lastName: userData.lastName,
          profileImageUrl: userData.profileImageUrl,
          username: userData.username,
          role: userData.role,
          updatedAt: new Date(),
        },
      })
      .returning();
    return result[0];
  }

  async updateUserStats(userId: string, contributions?: number, hearts?: number): Promise<void> {
    const updates: any = {};
    if (contributions !== undefined) {
      updates.contributionsCount = sql`${users.contributionsCount} + ${contributions}`;
    }
    if (hearts !== undefined) {
      updates.heartsReceived = sql`${users.heartsReceived} + ${hearts}`;
    }

    if (Object.keys(updates).length > 0) {
      await this.db.update(users).set(updates).where(eq(users.id, userId));
    }
  }

  // Rooms
  async getRoom(id: string): Promise<Room | undefined> {
    const result = await this.db.select().from(rooms).where(eq(rooms.id, id)).limit(1);
    return result[0];
  }

  async getRoomByCode(code: string): Promise<Room | undefined> {
    const result = await this.db.select().from(rooms).where(eq(rooms.code, code)).limit(1);
    return result[0];
  }

  async createRoom(room: InsertRoom): Promise<Room> {
    const result = await this.db.insert(rooms).values(room).returning();
    return result[0];
  }

  async getPublicRooms(): Promise<Room[]> {
    return await this.db.select().from(rooms).where(eq(rooms.isPrivate, false)).orderBy(desc(rooms.createdAt));
  }

  async updateRoomMemberCount(roomId: string, count: number): Promise<void> {
    await this.db.update(rooms).set({ memberCount: count }).where(eq(rooms.id, roomId));
  }

  // Stories
  async getStory(id: string): Promise<Story | undefined> {
    const result = await this.db.select().from(stories).where(eq(stories.id, id)).limit(1);
    return result[0];
  }

  async createStory(story: InsertStory): Promise<Story> {
    // Get next sequence number for this chain
    const maxSeq = await this.db.select({ max: sql<number>`max(${stories.sequence})` })
      .from(stories)
      .where(eq(stories.chainId, story.chainId));

    const sequence = (maxSeq[0]?.max || 0) + 1;

    const result = await this.db.insert(stories).values({
      ...story,
      sequence
    }).returning();

    return result[0];
  }

  async getStoriesByChain(chainId: number): Promise<Story[]> {
    return await this.db.select().from(stories)
      .where(eq(stories.chainId, chainId))
      .orderBy(stories.sequence);
  }

  async getStoriesByRoom(roomId: string): Promise<Story[]> {
    return await this.db.select().from(stories)
      .where(eq(stories.roomId, roomId))
      .orderBy(desc(stories.createdAt));
  }

  async getLatestStoryChains(limit = 10): Promise<StoryChain[]> {
    const chainsData = await this.db
      .select({
        chainId: stories.chainId,
        storyCount: sql<number>`count(*)`,
        lastUpdated: sql<Date>`max(${stories.createdAt})`,
        stories: sql<Story[]>`json_agg(
          json_build_object(
            'id', ${stories.id},
            'chainId', ${stories.chainId},
            'roomId', ${stories.roomId},
            'content', ${stories.content},
            'authorId', ${stories.authorId},
            'authorName', ${stories.authorName},
            'sequence', ${stories.sequence},
            'hearts', ${stories.hearts},
            'comments', ${stories.comments},
            'createdAt', ${stories.createdAt}
          ) ORDER BY ${stories.sequence}
        )`
      })
      .from(stories)
      .groupBy(stories.chainId)
      .orderBy(desc(sql`max(${stories.createdAt})`))
      .limit(limit);

    return chainsData.map(chain => ({
      chainId: chain.chainId,
      storyCount: chain.storyCount,
      lastUpdated: chain.lastUpdated,
      stories: chain.stories
    }));
  }

  async toggleHeart(storyId: string, userId: string): Promise<boolean> {
    const existing = await this.db.select().from(hearts)
      .where(and(eq(hearts.storyId, storyId), eq(hearts.userId, userId)))
      .limit(1);

    if (existing.length > 0) {
      // Remove heart
      await this.db.delete(hearts)
        .where(and(eq(hearts.storyId, storyId), eq(hearts.userId, userId)));

      await this.db.update(stories)
        .set({ hearts: sql`${stories.hearts} - 1` })
        .where(eq(stories.id, storyId));

      return false;
    } else {
      // Add heart
      await this.db.insert(hearts).values({ storyId, userId });

      await this.db.update(stories)
        .set({ hearts: sql`${stories.hearts} + 1` })
        .where(eq(stories.id, storyId));

      return true;
    }
  }

  async getNextChainId(): Promise<number> {
    const result = await this.db.select({ max: sql<number>`coalesce(max(${stories.chainId}), 0) + 1` }).from(stories);
    return result[0]?.max || 1;
  }

  // Themes
  async getDailyTheme(): Promise<Theme | undefined> {
    const result = await this.db.select().from(themes)
      .where(and(eq(themes.isDaily, true), eq(themes.isActive, true)))
      .limit(1);
    return result[0];
  }

  async getAllThemes(): Promise<Theme[]> {
    return await this.db.select().from(themes).where(eq(themes.isActive, true));
  }

  // Community
  async getCommunityStats(): Promise<CommunityStats> {
    const [storiesCount, usersCount, heartsCount] = await Promise.all([
      this.db.select({ count: sql<number>`count(*)` }).from(stories),
      this.db.select({ count: sql<number>`count(*)` }).from(users),
      this.db.select({ count: sql<number>`sum(${stories.hearts})` }).from(stories)
    ]);

    // Daily contributions (stories from today)
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const dailyContributions = await this.db.select({ count: sql<number>`count(*)` })
      .from(stories)
      .where(sql`${stories.createdAt} >= ${today}`);

    return {
      totalStories: storiesCount[0]?.count || 0,
      activeUsers: usersCount[0]?.count || 0,
      totalHearts: heartsCount[0]?.count || 0,
      dailyContributions: dailyContributions[0]?.count || 0,
    };
  }

  async getCookiesPicks(): Promise<CookiesPick[]> {
    return await this.db.select().from(cookiesPicks)
      .where(eq(cookiesPicks.isFeatured, true))
      .orderBy(desc(cookiesPicks.createdAt));
  }
}

// Use database storage if DATABASE_URL is available, otherwise fall back to memory
export const storage = process.env.DATABASE_URL ? new DatabaseStorage() : new MemStorage();

