import { useState } from "react";
import { Sparkles, Loader2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";

interface AIAssistProps {
  currentStory: string;
  onSuggestion: (suggestion: string) => void;
  isDisabled?: boolean;
}

export function AIAssist({ currentStory, onSuggestion, isDisabled }: AIAssistProps) {
  const [isGenerating, setIsGenerating] = useState(false);
  const { toast } = useToast();

  const generateSuggestion = async () => {
    if (isDisabled || isGenerating || !currentStory.trim()) return;

    setIsGenerating(true);

    try {
      const response = await fetch('/api/ai/continue-story', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ storyContext: currentStory }),
      });

      if (!response.ok) throw new Error('AI suggestion failed');

      const { continuation } = await response.json();
      onSuggestion(continuation);

      toast({
        title: "AI suggestion generated",
        description: "A creative continuation has been suggested for your story.",
      });
    } catch (error) {
      toast({
        title: "AI suggestion failed",
        description: "Unable to generate story continuation. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsGenerating(false);
    }
  };

  return (
    <Button
      variant="ghost"
      size="sm"
      onClick={generateSuggestion}
      disabled={isDisabled || isGenerating || !currentStory.trim()}
    >
      {isGenerating ? (
        <>
          <Loader2 className="w-4 h-4 animate-spin mr-2" />
          Thinking...
        </>
      ) : (
        <>
          <Sparkles className="w-4 h-4 mr-2" />
          AI Assist
        </>
      )}
    </Button>
  );
}

client/src/components/export-story.tsx.content:
import { useState } from "react";
import { Download, FileText, Image } from "lucide-react";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { useToast } from "@/hooks/use-toast";

interface ExportStoryProps {
  chainId: string;
  storyTitle: string;
}

export function ExportStory({ chainId, storyTitle }: ExportStoryProps) {
  const [isExporting, setIsExporting] = useState(false);
  const { toast } = useToast();

  const exportStory = async (format: 'pdf' | 'image') => {
    setIsExporting(true);

    try {
      const response = await fetch(`/api/export/${format}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ chainId, title: storyTitle }),
      });

      if (!response.ok) throw new Error('Export failed');

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.style.display = 'none';
      a.href = url;
      a.download = `${storyTitle.replace(/[^a-z0-9]/gi, '_').toLowerCase()}.${format === 'pdf' ? 'pdf' : 'png'}`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);

      toast({
        title: "Story exported",
        description: `Your story has been saved as a ${format.toUpperCase()} file.`,
      });
    } catch (error) {
      toast({
        title: "Export failed",
        description: "Unable to export story. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsExporting(false);
    }
  };

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button variant="ghost" size="icon" disabled={isExporting}>
          <Download className="w-4 h-4" />
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent>
        <DropdownMenuItem onClick={() => exportStory('pdf')}>
          <FileText className="w-4 h-4 mr-2" />
          Export as PDF
        </DropdownMenuItem>
        <DropdownMenuItem onClick={() => exportStory('image')}>
          <Image className="w-4 h-4 mr-2" />
          Export as Image
        </DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

client/src/components/room-manager.tsx.content:
import { useState } from "react";
import { useMutation, useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Checkbox } from "@/components/ui/checkbox";
import { Separator } from "@/components/ui/separator";
import { PlusCircle, DoorOpen, ChevronRight, LogIn } from "lucide-react";
import { api } from "@/lib/api";
import { queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { User, Room } from "@shared/schema";

interface RoomManagerProps {
  currentUser: User;
}

export default function RoomManager({ currentUser }: RoomManagerProps) {
  const [roomName, setRoomName] = useState("");
  const [roomPrompt, setRoomPrompt] = useState("");
  const [isPrivate, setIsPrivate] = useState(false);
  const [isThemed, setIsThemed] = useState(false);
  const [roomCode, setRoomCode] = useState("");
  const { toast } = useToast();

  // Fetch public rooms
  const { data: publicRooms = [] } = useQuery<Room[]>({
    queryKey: ["/api/rooms/public"],
  });

  // Create room mutation
  const createRoomMutation = useMutation({
    mutationFn: api.createRoom,
    onSuccess: (room) => {
      queryClient.invalidateQueries({ queryKey: ["/api/rooms/public"] });
      toast({
        title: "Room Created!",
        description: `Room "${room.name}" created with code: ${room.code}`,
      });
      // Reset form
      setRoomName("");
      setRoomPrompt("");
      setIsPrivate(false);
      setIsThemed(false);
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create room. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Join room mutation
  const joinRoomMutation = useMutation({
    mutationFn: api.joinRoom,
    onSuccess: (room) => {
      toast({
        title: "Joined Room!",
        description: `Welcome to "${room.name}"`,
      });
      setRoomCode("");
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Room not found or unable to join.",
        variant: "destructive",
      });
    },
  });

  const handleCreateRoom = () => {
    if (!roomName.trim() || !roomPrompt.trim()) {
      toast({
        title: "Missing Information",
        description: "Please fill in room name and starting prompt.",
        variant: "destructive",
      });
      return;
    }

    createRoomMutation.mutate({
      name: roomName,
      prompt: roomPrompt,
      isPrivate,
      isThemed,
      theme: isThemed ? "Custom Theme" : "",
      creatorId: currentUser.id,
    });
  };

  const handleJoinRoom = () => {
    if (!roomCode.trim() || roomCode.length !== 6) {
      toast({
        title: "Invalid Code",
        description: "Please enter a valid 6-character room code.",
        variant: "destructive",
      });
      return;
    }

    joinRoomMutation.mutate(roomCode.toUpperCase());
  };

  return (
    <Card className="bg-white shadow-story border border-light-beige/30">
      <CardContent className="p-8">
        <div className="text-center mb-8">
          <h3 className="font-serif text-2xl font-semibold text-gray-800 mb-2">Create Your Story Circle</h3>
          <p className="text-gray-600">Start a private room with friends or join existing story circles</p>
        </div>

        <div className="grid md:grid-cols-2 gap-8">
          {/* Create Room */}
          <div className="space-y-4">
            <h4 className="font-medium text-gray-800 flex items-center">
              <PlusCircle className="text-warm-teal mr-2 w-5 h-5" />
              Create New Room
            </h4>

            <div className="space-y-4">
              <div>
                <Label htmlFor="roomName" className="text-sm text-gray-600">Room Name</Label>
                <Input 
                  id="roomName"
                  type="text" 
                  placeholder="e.g., Midnight Storytellers" 
                  value={roomName}
                  onChange={(e) => setRoomName(e.target.value)}
                  className="mt-1"
                />
              </div>

              <div>
                <Label htmlFor="roomPrompt" className="text-sm text-gray-600">Starting Prompt</Label>
                <Textarea 
                  id="roomPrompt"
                  placeholder="Someone somewhere is..." 
                  value={roomPrompt}
                  onChange={(e) => setRoomPrompt(e.target.value)}
                  className="mt-1 resize-none"
                  rows={2}
                />
              </div>

              <div className="flex items-center space-x-6">
                <div className="flex items-center space-x-2">
                  <Checkbox 
                    id="private"
                    checked={isPrivate}
                    onCheckedChange={(checked) => setIsPrivate(checked as boolean)}
                  />
                  <Label htmlFor="private" className="text-sm text-gray-600">Private (invite only)</Label>
                </div>
                <div className="flex items-center space-x-2">
                  <Checkbox 
                    id="themed"
                    checked={isThemed}
                    onCheckedChange={(checked) => setIsThemed(checked as boolean)}
                  />
                  <Label htmlFor="themed" className="text-sm text-gray-600">Themed</Label>
                </div>
              </div>

              <Button 
                onClick={handleCreateRoom}
                disabled={createRoomMutation.isPending}
                className="w-full bg-warm-teal text-white hover:bg-warm-teal/90"
              >
                {createRoomMutation.isPending ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Creating...
                  </>
                ) : (
                  <>
                    Create Room
                    <DoorOpen className="w-4 h-4 ml-2" />
                  </>
                )}
              </Button>
            </div>
          </div>

          {/* Join Room */}
          <div className="space-y-4">
            <h4 className="font-medium text-gray-800 flex items-center">
              <DoorOpen className="text-warm-brown mr-2 w-5 h-5" />
              Join Existing Room
            </h4>

            <div className="space-y-4">
              <div>
                <Label htmlFor="roomCode" className="text-sm text-gray-600">Room Code</Label>
                <Input 
                  id="roomCode"
                  type="text" 
                  placeholder="Enter 6-digit code" 
                  value={roomCode}
                  onChange={(e) => setRoomCode(e.target.value.toUpperCase())}
                  className="mt-1 uppercase tracking-widest text-center"
                  maxLength={6}
                />
              </div>

              <Button 
                onClick={handleJoinRoom}
                disabled={joinRoomMutation.isPending || roomCode.length !== 6}
                className="w-full bg-warm-brown text-white hover:bg-warm-brown/90"
              >
                {joinRoomMutation.isPending ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Joining...
                  </>
                ) : (
                  <>
                    Join Room
                    <LogIn className="w-4 h-4 ml-2" />
                  </>
                )}
              </Button>
            </div>

            {/* Active Rooms Preview */}
            {publicRooms.length > 0 && (
              <>
                <Separator className="my-6" />
                <div>
                  <h5 className="text-sm font-medium text-gray-700 mb-3">Popular Public Rooms</h5>
                  <div className="space-y-2">
                    {publicRooms.slice(0, 3).map((room) => (
                      <Button
                        key={room.id}
                        variant="ghost"
                        className="w-full text-left p-3 bg-gray-50 hover:bg-gray-100 h-auto"
                      >
                        <div className="flex justify-between items-center w-full">
                          <div>
                            <p className="text-sm font-medium text-gray-800">{room.name}</p>
                            <p className="text-xs text-gray-500">
                              {room.memberCount} active writer{room.memberCount !== 1 ? 's' : ''}
                            </p>
                          </div>
                          <ChevronRight className="w-4 h-4 text-gray-400" />
                        </div>
                      </Button>
                    ))}
                  </div>
                </div>
              </>
            )}
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/story-chain.tsx.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Heart, MessageCircle, Share, Bookmark, User } from "lucide-react";
import { api } from "@/lib/api";
import { queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { ExportStory } from "@/components/export-story";
import type { StoryChain as StoryChainType, User as UserType } from "@shared/schema";

interface StoryChainProps {
  chain: StoryChainType;
  currentUser: UserType;
  onContinue: (content: string, chainId: number) => void;
}

const avatarColors = [
  "from-purple-400 to-pink-400",
  "from-blue-400 to-teal-400", 
  "from-green-400 to-emerald-400",
  "from-orange-400 to-red-400",
  "from-indigo-400 to-purple-400",
  "from-rose-400 to-pink-400",
];

export default function StoryChain({ chain, currentUser, onContinue }: StoryChainProps) {
  const [showContinueInput, setShowContinueInput] = useState(false);
  const [continueText, setContinueText] = useState("");
  const { toast } = useToast();

  const heartMutation = useMutation({
    mutationFn: (storyId: string) => api.toggleHeart(storyId),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/stories/chains"] });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update heart. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleContinue = () => {
    if (!continueText.trim()) return;

    onContinue(continueText, chain.chainId);
    setContinueText("");
    setShowContinueInput(false);
  };

  const handleHeart = (storyId: string) => {
    heartMutation.mutate(storyId);
  };

  const getAvatarColor = (index: number) => {
    return avatarColors[index % avatarColors.length];
  };

  return (
    <Card className="bg-white dark:bg-gray-800 shadow-story border border-light-beige/30 dark:border-gray-700/30 overflow-hidden">
      <CardContent className="p-6">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center space-x-3">
            <Badge className="bg-warm-teal/20 text-warm-teal hover:bg-warm-teal/30">
              Chain #{chain.chainId}
            </Badge>
            <span className="text-gray-500 dark:text-gray-400 text-sm">
              Started {new Date(chain.createdAt).toLocaleDateString()}
            </span>
          </div>
          <div className="flex items-center space-x-2">
            <ExportStory chainId={chain.chainId} storyTitle={`Story Chain #${chain.chainId}`} />
            <Button variant="ghost" size="icon">
              <Bookmark className="w-4 h-4" />
            </Button>
          </div>
        </div>

        {/* Story Chain Lines */}
        <div className="space-y-3">
          {chain.stories.map((story, index) => (
            <div 
              key={story.id} 
              className={`flex items-start space-x-3 p-3 rounded-xl transition-colors ${
                index === 0 ? 'bg-story-gradient-start/30' : 'hover:bg-gray-50'
              }`}
            >
              <div className={`w-8 h-8 bg-gradient-to-br ${getAvatarColor(index)} rounded-full flex items-center justify-center text-white text-xs font-medium`}>
                {story.authorName.charAt(0).toUpperCase()}
              </div>
              <div className="flex-1">
                <p className="text-gray-800 leading-relaxed">{story.content}</p>
                <div className="flex items-center justify-between mt-2">
                  <span className="text-xs text-gray-500">by {story.authorName}</span>
                  <div className="flex items-center space-x-2">
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => handleHeart(story.id)}
                      disabled={heartMutation.isPending}
                      className="text-gray-500 hover:text-red-500 transition-colors h-6 px-2"
                    >
                      <Heart className="w-3 h-3 mr-1" />
                      <span className="text-xs">{story.hearts}</span>
                    </Button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Continue Story Input */}
        {showContinueInput && (
          <div className="mt-4 p-4 bg-accent rounded-xl">
            <Textarea
              placeholder="Continue the story..."
              value={continueText}
              onChange={(e) => setContinueText(e.target.value)}
              className="mb-3 resize-none"
              rows={3}
            />
            <div className="flex justify-between items-center">
              <span className="text-sm text-gray-500">
                {280 - continueText.length} characters left
              </span>
              <div className="space-x-2">
                <Button 
                  variant="ghost" 
                  size="sm"
                  onClick={() => setShowContinueInput(false)}
                >
                  Cancel
                </Button>
                <Button 
                  size="sm"
